<script>
import ResourceItem from '@/components/ResourceItem.vue'
import arraySort from 'array-sort'
import CONSTANTS from '../../constants'
export default {
    components: {
       ResourceItem
    },
    computed: {
        items () {
            if(this.$store.state.inventory.length > 0) {
                return arraySort([...this.$store.state.inventory], 'balance', {reverse: true})
                .filter(v => v.balance > 0)
            } else return {}
        }
    },
    methods: {
        openItemInExplorer(symbol) {
            window.open(`${CONSTANTS.chainExplorer}address/${CONSTANTS.economicPolicy.assets[symbol].address}`, "_blank");
        },
        closeModal() {
            this.$emit('close')
        }
    }
}
</script>

