</template>


<script>
import CONSTANTS from "../../constants.json"
import axios from 'axios'

export default {
    props:['state','playerIs', 'playerTurn', 'lastTurnTimestamp', 'pickingRound', 'player0PickingInsertions', 'player1PickingInsertions'],
    data() {
        return {
            date: Date.now(),
            dateInterval: undefined,
            turnTimeout: CONSTANTS.pickingTurnTimeout,
            cardsVal: 0,
            spaceships: [...CONSTANTS.freeSpaceshipsRotation],
            // myPicks: [{},{},{},{},{},{},{},{},{}],
            // enemyPicks: [{},{},{},{},{},{},{},{},{}],
            turnSfx: require('../assets/sfx/turn.webm'),
            selected: undefined,
            hovered: undefined,
            playerProfile: undefined,
            attackInfoIcon: 'https://res.cloudinary.com/station0x/image/upload/v1644548304/encouter/elements/icons/attack_kwg82o.svg',
            moveInfoIcon: 'https://res.cloudinary.com/station0x/image/upload/v1644548304/encouter/elements/icons/move_hngi7m.svg',
            repairInfoIcon: 'https://res.cloudinary.com/station0x/image/upload/v1644549515/encouter/elements/icons/repair_lj60dt.svg',
            shockInfoIcon: 'https://res.cloudinary.com/station0x/image/upload/v1644548304/encouter/elements/icons/shock_fiygbi.svg'
        }
    },
    computed: {
        myPicks () {
            if(!this.state) return [{},{},{},{},{},{},{},{},{}]
            return this.state[this.playerRow]
        },
        enemyPicks () {
            if(!this.state) return [{},{},{},{},{},{},{},{},{}]
            return this.state[this.enemyRow]
        },
        spaceshipsRotation() {
            let spaceships = [...this.spaceships]
            return spaceships.map((spaceship) => {
                return {
                    type: spaceship,
                    image: require(`../assets/blue/${spaceship}.webp`),
                    info: CONSTANTS.spaceshipsAttributes[spaceship]
                }
            })
        },
        formattedAddress() {
            return this.$store.state.address.slice(0, 5) + '...' + this.$store.state.address.slice(-4)
        },
        playerAlias() {
            return this.playerProfile === undefined ? 'Captain' : this.playerProfile.playerAlias
        },
        isMyTurn() {
            return this.playerTurn === this.playerIs
        },
        countdown() {
            if (this.lastTurnTimestamp === undefined) return this.turnTimeout
            return Math.max(Math.floor((this.lastTurnTimestamp + (this.turnTimeout * 1000) - this.date) / 1000), 0)
        },
        myPickingCount() {
            return this.playerIs === 0 ? this.player0PickingInsertions : this.player1PickingInsertions
        },
        insertionsAllowed() {
            return Math.max(CONSTANTS.pickingInsertionsAllowedPerTurn - this.myPickingCount, 0)
        },
        playerRow() {
            return this.playerIs === 0 ? 7 : 1
        },
        enemyRow() {
            return this.playerIs === 0 ? 1 : 7
        },
        cssProps() {
            let styles = {}
            if(this.isMyTurn) {
                styles['--gradient-color'] = 'rgb(16, 27, 37)'
            } else {
                styles['--gradient-color'] = 'rgb(32, 14, 14)'
            }
            return styles
        },
    },
    methods: {
        playSound(sfx) {
			var audio = new Audio(sfx)
			try {
				audio.play()
			} catch(e) { console.log(e) }
		},
        spaceshipImg(hex) {
            if(hex.type === undefined) return require('../assets/img/blank.gif')
            else return hex.owner === this.playerIs ? require(`../assets/blue/${hex.type}.webp`) : require(`../assets/red/${hex.type}.webp`)
        },
        hexImg(hex, index) {
            if(this.isMyTurn) {
                if(hex.type === undefined && this.selected === undefined) return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/hex-slot_buo7ot.svg'
                else if(hex.type === undefined && this.selected !== undefined) return 'https://res.cloudinary.com/station0x/image/upload/v1645091327/encouter/elements/hexes/insertion-slot_rrtdnc.svg'
                else if(hex.type !== undefined && this.hovered === index && hex.canRemove === true) return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/removable-slot_uklatc.svg'
                else if(hex.type !== undefined && hex.canRemove === false) return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/locked-slot_h8vptu.svg'
                else return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/occupied-slot_mlrzhp.svg'
            } else {
                if(hex.type !== undefined && hex.canRemove === false) return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/locked-slot_h8vptu.svg'
                else return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/hex_hh2ews.svg'
            }
        },
        enemyHexImg(hex) {
            if(hex.type !== undefined && hex.canRemove === false) return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/locked-slot_h8vptu.svg'
            else return 'https://res.cloudinary.com/station0x/image/upload/v1645091325/encouter/elements/hexes/hex_hh2ews.svg'
        },
        renderHexImg(hex, index) {
            let classes = 'hex-bg '
            if(this.hovered === index) classes += 'hex-selectable '
            if(this.selected !== undefined) {
                if(hex.length > 0) classes += 'occupied '
                else classes += 'allowed-position '
            }
            return classes
        },
        endTurn() {
			this.playSound(this.turnSfx)
            if(this.insertionsAllowed > 0 && this.pickingRound > 2) {
                const winner = this.playerIs === 0 ? 1 : 0
			    this.$store.commit('setWinner', winner)
            }
            this.$store.commit('endTurn')
			this.$store.dispatch('enqueue', () => axios.get('/api/match/endTurn', {
				params:{
					signature:this.$store.state.signature,
					matchId: this.$store.state.matchId
				}
			}))
    	},
        endEnemyTurn() {
            this.$store.commit('endTurn')
			this.$store.dispatch('enqueue', axios.get('/api/match/endEnemyTurn', {
				params:{
					signature:this.$store.state.signature,
					matchId: this.$store.state.matchId
				}
			}))
    	},
        spaceshipAddedSnackbar(type) {
            this.$buefy.snackbar.open({
                duration: 2000,
                message: `${type} was added to your fleet.`
                ,
                type: 'is-success',
                position: 'is-top',
                queue: true
            })
        },
        spaceshipRemovedSnackbar(type) {
            this.$buefy.snackbar.open({
                duration: 2000,
                message: `${type} was removed to your fleet.`
                ,
                type: 'is-success',
                position: 'is-top',
                queue: true
            })
        },
        spaceshipMaxReachedSnackbar() {
            this.$buefy.snackbar.open({
                duration: 3000,
                message: 'Cannot add more than two spaceships of the same type to your fleet.',
                type: 'is-danger',
                position: 'is-top',
                queue: true
            })
        },
        capitalize(str) {
			if(str) {
				const lower = str.toLowerCase()
				return str.charAt(0).toUpperCase() + lower.slice(1)
			}
			return str
		},
        selectSpaceship(spaceshipObj) {
            if(this.insertionsAllowed > 0) {
                if(this.selected === undefined) this.selected = spaceshipObj
                else if(this.selected !== undefined && this.selected.type !== spaceshipObj.type) this.selected = spaceshipObj
                else this.selected = undefined
            }
        },
        insertSpaceship(spaceship, hexId, hex) {
            if(this.isMyTurn && hex.type === undefined && this.insertionsAllowed > 0) {
                let sameInserted = 0
                this.myPicks.forEach((element) => {
                    if(element.type === spaceship.type) sameInserted++
                });
                if(sameInserted < 2 && this.insertionsAllowed > 0) {
                    // Insert Spaceship
                    let spaceshipObj = { type: spaceship.type, owner: this.playerIs }
                    this.$store.dispatch('insertSpaceship', {spaceshipObj, to: {x: hexId, y: this.playerRow}, playerIs: this.playerIs})
                    sameInserted++
                    this.$store.dispatch('enqueue', () => axios.get('/api/match/pickingAction', {
                        params:{
                            signature:this.$store.state.signature,
                            matchId: this.$store.state.matchId,
                            action: 'insert',
                            posX: hexId,
                            spaceshipType: spaceship.type
                        }
                    }))
                    if(sameInserted === 2) {
                        this.spaceships = this.spaceships.filter(spaceshipType => spaceshipType !== spaceship.type)
                    }
                    this.spaceshipAddedSnackbar(this.capitalize(spaceship.type))
                } else {
                    this.spaceshipMaxReachedSnackbar(this.capitalize(spaceship.type))
                }
            }
        },
        removeSpaceship(hexId) {
            if(this.myPicks[hexId].canRemove === true) {
                let spaceshipType = this.myPicks[hexId].type
                if(!this.spaceships.includes(spaceshipType)) {
                    this.spaceships.unshift(spaceshipType)
                }
                this.$store.dispatch('removeSpaceship', {from: {x: hexId, y: this.playerRow}, playerIs: this.playerIs})
                this.$store.dispatch('enqueue', () => axios.get('/api/match/pickingAction', {
                    params:{
                        signature:this.$store.state.signature,
                        matchId: this.$store.state.matchId,
                        action: 'remove',
                        posX: hexId
                    }
                }))               
            }
        },
        spaceshipInsertedTwice(type) {
            let sameInserted = 0
            this.myPicks.forEach((element) => {
                if(element.type === type) sameInserted++
            });
            return sameInserted === 2
        },
        selectHexPosition(hex, index) {
            if(this.selected !== undefined) {
                this.insertSpaceship(this.selected, index, hex)
                this.selected = undefined
            } else if(hex.type !== undefined) {
                this.removeSpaceship(index)
            }
        },
        hoverHex(index) {
            this.hovered = index
        },
        async fetchProfile(address) {
            const res = await axios.get('/api/player/fetchPlayerProfile', {
                params:{
                    address: address
                }
            })
            this.playerProfile = res.data.playerDoc
        },
        leftiestEle() {
            if(this.hovered === undefined) return false
            else {
                return this.hovered.index === this.cardsVal
            }
        },
        rightiestEle() {
            if(this.hovered === undefined) return false
            else {
                return this.hovered.index === this.cardsVal + 7
            }
        }
    },
    watch: {
        countdown(newCountdown) {
            if(newCountdown === 0 && !this.isMyTurn) {
                setTimeout(() => {
                    if(this.countdown === 0 && !this.isMyTurn) this.endEnemyTurn()
                }, 5000)
            }
            if(newCountdown === 0 && this.isMyTurn) {
                this.endTurn()
            }
        }
    },
    async created() {
        await this.fetchProfile(this.$store.state.address, false)
        if(this.countdown === 0 && this.isMyTurn) this.endTurn()
        const self = this
        this.dateInterval = setInterval(function () {
            self.date = Date.now()
        }, 1000)

        if(this.countdown === 0  && !this.isMyTurn) {
            this.endEnemyTurn()
        }
    },
    beforeDestroy () {
        clearInterval(this.dateInterval)
    }
}
</script>

